-- Create the base view V1\nCREATE VIEW V1\nWITH SCHEMABINDING\nAS\nSELECT 1 AS abc\nGO\n\n-- Create a unique clustered index on V1\nCREATE UNIQUE CLUSTERED INDEX IDX_V1 ON V1 (abc)\nGO\n\n-- Create the second view V2\nCREATE VIEW V2\nWITH SCHEMABINDING\nAS\nSELECT abc\nFROM dbo.V1\nGROUP BY abc\nGO\n\n-- Create a unique clustered index on V2\nCREATE UNIQUE CLUSTERED INDEX IDX_V2 ON V2 (abc)\nGO