CREATE TRIGGER trg_Audit\n   ON YourTable\n   AFTER INSERT, UPDATE, DELETE\n   AS\n   BEGIN\n       -- Insert audit data into AuditTable\n       INSERT INTO AuditTable (Operation, TableName, PrimaryKeyValue, ColumnName, OldValue, NewValue, ChangedBy, ChangeDate)\n       SELECT 'INSERT', 'YourTable', inserted.PrimaryKey, 'ColumnName', NULL, inserted.ColumnName, SYSTEM_USER, GETDATE()\n       FROM inserted;\n\n       -- Repeat for UPDATE and DELETE operations\n   END