CREATE TABLE DimensionTable (\n    id INT IDENTITY(1,1) PRIMARY KEY\n);\n\nCREATE TABLE FactTable (\n    source_id INT NOT NULL,\n    target_id INT NOT NULL,\n    CONSTRAINT FK_Fact_Source FOREIGN KEY (source_id) REFERENCES DimensionTable(id) ON DELETE NO ACTION ON UPDATE NO ACTION,\n    CONSTRAINT FK_Fact_Target FOREIGN KEY (target_id) REFERENCES DimensionTable(id) ON DELETE NO ACTION ON UPDATE NO ACTION\n);