DECLARE @TableName NVARCHAR(128) = 'YourTableName'\nDECLARE @ConstraintName NVARCHAR(128)\nDECLARE @sql NVARCHAR(MAX)\n\n-- Cursor to iterate through each default constraint on the specified table\nDECLARE cur CURSOR FOR\nSELECT default_constraints.name\nFROM sys.default_constraints\nJOIN sys.tables ON default_constraints.parent_object_id = tables.object_id\nWHERE tables.name = @TableName\n\nOPEN cur\nFETCH NEXT FROM cur INTO @ConstraintName\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Dynamically build the SQL command to drop the constraint\n    SET @sql = 'ALTER TABLE ' + @TableName + ' DROP CONSTRAINT ' + @ConstraintName\n    EXEC sp_executesql @sql\n\n    FETCH NEXT FROM cur INTO @ConstraintName\nEND\n\nCLOSE cur\nDEALLOCATE cur