CREATE TABLE LineCharts (\n    ChartId INT PRIMARY KEY FOREIGN KEY REFERENCES Charts(ChartId),\n    Thickness INT,\n    YAxis VARCHAR(50),\n    XAxis VARCHAR(50)\n);\n\nCREATE TABLE PieCharts (\n    ChartId INT PRIMARY KEY FOREIGN KEY REFERENCES Charts(ChartId),\n    Radius INT\n);\n\nCREATE TABLE BarCharts (\n    ChartId INT PRIMARY KEY FOREIGN KEY REFERENCES Charts(ChartId),\n    Width INT,\n    Border VARCHAR(20)\n);