CREATE TABLE Charts (\n    ChartId INT PRIMARY KEY,\n    ReportId INT FOREIGN KEY REFERENCES Reports(ReportId),\n    ChartType VARCHAR(50),\n    Thickness INT NULL,\n    YAxis VARCHAR(50) NULL,\n    XAxis VARCHAR(50) NULL,\n    Radius INT NULL,\n    Width INT NULL,\n    Color VARCHAR(20),\n    Border VARCHAR(20) NULL,\n    -- Other common columns\n    CONSTRAINT CHK_ChartType CHECK (ChartType IN ('Line', 'Pie', 'Bar'))\n);