CREATE TRIGGER trg_CheckCondition\nON YourTable\nAFTER INSERT, UPDATE\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    DECLARE @errorFound BIT = 0;\n    DECLARE @errorMessage NVARCHAR(4000);\n\n    BEGIN TRY\n        DECLARE cursor_ins CURSOR FOR\n        SELECT * FROM INSERTED;\n        \n        DECLARE @yourColumn INT;\n\n        OPEN cursor_ins;\n        FETCH NEXT FROM cursor_ins INTO @yourColumn;\n\n        WHILE @@FETCH_STATUS = 0\n        BEGIN\n            -- Check your specific condition here\n            IF @yourColumn < 0\n            BEGIN\n                SET @errorFound = 1;\n                SET @errorMessage = 'Condition not met: yourColumn must be non-negative.';\n                BREAK;\n            END\n\n            FETCH NEXT FROM cursor_ins INTO @yourColumn;\n        END\n\n        CLOSE cursor_ins;\n        DEALLOCATE cursor_ins;\n\n        IF @errorFound = 1\n        BEGIN\n            -- Rollback the transaction and raise an error\n            ROLLBACK TRANSACTION;\n            THROW 50000, @errorMessage, 1;\n        END\n    END TRY\n    BEGIN CATCH\n        -- Rollback transaction in case of errors\n        IF @@TRANCOUNT > 0\n        BEGIN\n            ROLLBACK TRANSACTION;\n        END\n\n        -- Rethrow the error\n        THROW;\n    END CATCH\nEND;