SELECT \n    homes.home_id,\n    homes.address,\n    homes.city,\n    homes.state,\n    homes.zip,\n    home_photos.photo_id,\n    home_photos.photo_url_dir\nFROM \n    homes\nLEFT JOIN \n    home_photos\n    ON homes.home_id = home_photos.home_id\n    AND homes.primary_photo_group_id = home_photos.photo_group_id\n    AND home_photos.home_photo_type_id = 2\nWHERE \n    homes.primary_photo_group_id = 0\n    OR home_photos.photo_id IS NOT NULL;