DELIMITER $$\n\nDROP PROCEDURE IF EXISTS SP2 $$\n\nCREATE PROCEDURE SP2()\nBEGIN\n    DECLARE InputCustomerID INT;\n    DECLARE InputOrderID INT;\n\n    -- Fetch InputCustomerID\n    SELECT CustomerID INTO InputCustomerID\n    FROM Customers\n    WHERE CustomerID NOT IN (SELECT FK_CustomerID FROM CustomerOrders)\n      AND IsApproved = TRUE\n      AND CustomerID IN (\n            SELECT FK_CustomerID \n            FROM CustomerProductOrders \n            WHERE DATE(OrderStartDate) = DATE(NOW())\n      )\n    LIMIT 1;  -- Ensure only one result is fetched\n\n    -- Fetch InputOrderID\n    SELECT OrderID INTO InputOrderID\n    FROM CustomerOrders\n    WHERE FK_CustomerID NOT IN (SELECT FK_CustomerID FROM CustomerProductOrders)\n      AND IsApproved = TRUE\n      AND DATE(OrderStartDate) = DATE(NOW())\n    LIMIT 1;  -- Ensure only one result is fetched\n\n    -- Call SP1 with the fetched values\n    CALL SP1(InputCustomerID, InputOrderID);\nEND $$\n\nDELIMITER ;