-- Assuming synthetic primary keys\nALTER TABLE foo ADD PRIMARY KEY (foo_id);\nALTER TABLE bar ADD PRIMARY KEY (bar_id);\n\n-- Create indexes on frequently queried columns\nCREATE INDEX idx_foo_fId ON foo(fId);\nCREATE INDEX idx_bar_cId ON bar(cId);\n\n-- If multi-part uniqueness is necessary\nALTER TABLE foo ADD CONSTRAINT unique_foo UNIQUE (CustomerId, OtherKey1, OtherKey2);\nALTER TABLE bar ADD CONSTRAINT unique_bar UNIQUE (CustomerId, OtherKey1, OtherKey2);\n\n-- Example Query with optimizations\nSELECT DISTINCT f.FIELDNAME AS fieldName\nFROM foo f\nINNER JOIN bar b ON f.id = b.fId\nWHERE b.cId = @id;