SELECT \n    u.fname, \n    u.lname, \n    u.role, \n    u.username, \n    r.building AS ra_building, \n    r.room AS ra_room, \n    r.region AS ra_region, \n    a.office AS ac_office, \n    a.office_num AS ac_office_num\nFROM \n    users u \nLEFT JOIN \n    ra_ca r ON u.username = r.username \nLEFT JOIN \n    area_coord a ON u.username = a.username\nWHERE \n    u.fname LIKE '%search_string%' OR \n    u.lname LIKE '%search_string%' OR \n    r.building LIKE '%search_string%' OR \n    r.region LIKE '%search_string%' OR \n    a.office_num LIKE '%search_string%'