SELECT uci.username, uci.cardName, uci.timeStamp AS firstTimeStamp, uci.msToAnswer\nFROM usercardinfo uci\nINNER JOIN (\n    SELECT username, cardName, MIN(timeStamp) AS firstTimeStamp\n    FROM usercardinfo\n    GROUP BY username, cardName\n) earliest ON uci.username = earliest.username AND uci.cardName = earliest.cardName AND uci.timeStamp = earliest.firstTimeStamp\nORDER BY uci.username, uci.cardName;