-- Create a function to generate sorted concatenation of artist IDs\nDELIMITER //\n\nCREATE FUNCTION GetSortedArtistIDs(GroupID INT) RETURNS VARCHAR(255)\nBEGIN\n    DECLARE sorted_ids VARCHAR(255);\n    \n    SELECT GROUP_CONCAT(ArtistID ORDER BY ArtistID SEPARATOR ',') INTO sorted_ids\n    FROM Artist_Group_Participation\n    WHERE GroupID = GroupID;\n    \n    RETURN sorted_ids;\nEND //\n\nDELIMITER ;\n\n-- Add a unique constraint using the function\nCREATE UNIQUE INDEX idx_unique_artist_group\nON Artist_Group_Participation (GetSortedArtistIDs(GroupID));