CREATE TABLE Persons (\n    PersonID int PRIMARY KEY,\n    FirstName varchar(255),\n    LastName varchar(255),\n    UNIQUE (FirstName, LastName)\n);\n\nCREATE TRIGGER trg_check_name\nBEFORE INSERT ON Persons\nFOR EACH ROW\nBEGIN\n    IF EXISTS (SELECT * FROM Persons WHERE FirstName = NEW.FirstName AND LastName = NEW.LastName) THEN\n        SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'Duplicate name entry';\n    END IF;\nEND;