-- Example of an hourly aggregation table\nCREATE TABLE hourly_hits (\n    hour_start TIMESTAMP PRIMARY KEY,\n    hit_count INT\n);\n\n-- Aggregation job (run every hour)\nINSERT INTO hourly_hits (hour_start, hit_count)\nSELECT DATE_FORMAT(event_time, '%Y-%m-%d %H:00:00') AS hour_start, COUNT(*)\nFROM hits\nWHERE event_time >= NOW() - INTERVAL 1 HOUR\nGROUP BY hour_start\nON DUPLICATE KEY UPDATE hit_count = VALUES(hit_count);