-- Set the path to include the required libraries\nSET PATH = QACOMMON, QAVISLIVE;\n\n-- Now you can write your query without specifying the library names\nSELECT DISTINCT GIDNBR, VSL00100.GRNAME \nFROM CMPGRID2 CMPGRID2 \nINNER JOIN VSL00100 VSL00100 \nON VSL00100.GRNO = CMPGRID2.GIDNBR \nAND VSL00100.GRSUB = CMPGRID2.GIDSUB \nAND VSL00100.GRLOC = CMPGRID2.GIDLOC \nAND VSL00100.GRPOOL = CMPGRID2.GIDPOL;