-- Assuming we have a Cars table and a Wheels table\n-- Cars table (CarID PK, CarName)\n-- Wheels table (WheelID PK, CarID FK, WheelType)\n\n-- Insert a new car\nINSERT INTO Cars (CarName)\nVALUES ('Toyota');\n\n-- Get the CarID of the newly inserted car\nDECLARE @CarID INT;\nSET @CarID = SCOPE_IDENTITY();\n\n-- Insert wheels associated with the new car\nINSERT INTO Wheels (CarID, WheelType)\nVALUES (@CarID, 'Alloy'), (@CarID, 'Alloy'), (@CarID, 'Alloy'), (@CarID, 'Alloy');