DECLARE @DocOrder1 INT, @DocOrder2 INT;\n\n   -- Get current orders\n   SELECT @DocOrder1 = Doc_Order FROM dbo.Documents WHERE Doc_Id = 1;\n   SELECT @DocOrder2 = Doc_Order FROM dbo.Documents WHERE Doc_Id = 12;\n\n   -- Switch orders\n   UPDATE dbo.Documents\n   SET Doc_Order = CASE \n                      WHEN Doc_Id = 1 THEN @DocOrder2\n                      WHEN Doc_Id = 12 THEN @DocOrder1\n                    END\n   WHERE Doc_Id IN (1, 12);