SELECT \n    AVG(Reponse.note) AS noteMoyenne, \n    Categorie.titre, \n    Autorisation.typeEvaluateur, \n    COUNT(DISTINCT Autorisation.id) AS nbEvaluateur\nFROM \n    reponses AS Reponse\nJOIN \n    questions AS Question ON Reponse.question_id = Question.id\nJOIN \n    categories AS Categorie ON Question.categorie_id = Categorie.id\nJOIN \n    autorisations AS Autorisation ON Reponse.evaluation_id = Autorisation.evaluation_id\nWHERE \n    Reponse.note != 0 AND\n    Reponse.evaluation_id = ? AND\n    Autorisation.evaluation_id = ? AND\n    Autorisation.complete = 1\nGROUP BY \n    Categorie.titre, \n    Autorisation.typeEvaluateur\nORDER BY \n    Categorie.id;