-- Format to 23/12/2008 00:00:00\nSELECT TO_CHAR(TO_DATE('2008.12.23 00:00:00', 'YYYY.MM.DD HH24:MI:SS'), 'DD/MM/YYYY HH24:MI:SS') AS formatted_date\nFROM dual;\n\n-- Format to 23-DEC-2008 00:00:00\nSELECT TO_CHAR(TO_DATE('2008.12.23 00:00:00', 'YYYY.MM.DD HH24:MI:SS'), 'DD-MON-YYYY HH24:MI:SS') AS formatted_date\nFROM dual;