CREATE TABLE signal_header (\n    signal_id NUMBER PRIMARY KEY,\n    start_time TIMESTAMP,\n    sample_rate NUMBER,\n    units VARCHAR2(50),\n    description VARCHAR2(255)\n);\n\nCREATE TABLE raw_signal_value (\n    value_id NUMBER PRIMARY KEY,\n    signal_id NUMBER,\n    offset_index NUMBER,\n    value NUMBER,\n    CONSTRAINT fk_signal\n        FOREIGN KEY (signal_id)\n        REFERENCES signal_header(signal_id)\n);