-- Replace 'YourTableName' and 'YourColumnName' with the actual table and column names\nDECLARE @tableName NVARCHAR(128) = 'YourTableName';\nDECLARE @columnName NVARCHAR(128) = 'YourColumnName';\nDECLARE @sql NVARCHAR(MAX);\n\n-- Check if the column is set to NOT NULL\nIF EXISTS (\n    SELECT 1\n    FROM INFORMATION_SCHEMA.COLUMNS\n    WHERE TABLE_NAME = @tableName\n      AND COLUMN_NAME = @columnName\n      AND IS_NULLABLE = 'NO'\n)\nBEGIN\n    -- Generate the SQL to alter the column to be NULL\n    SET @sql = 'ALTER TABLE ' + QUOTENAME(@tableName) + ' ALTER COLUMN ' + QUOTENAME(@columnName) + ' <data_type> NULL';\n\n    -- Execute the generated SQL\n    EXEC sp_executesql @sql;\nEND