CREATE OR REPLACE PROCEDURE secure_insert(p_data IN VARCHAR2) AS\n    app_name VARCHAR2(100);\nBEGIN\n    SELECT sys_context('USERENV', 'CLIENT_PROGRAM_NAME') INTO app_name FROM dual;\n    IF app_name != 'ourTool.exe' THEN\n        RAISE_APPLICATION_ERROR(-20001, 'Unauthorized application.');\n    END IF;\n    \n    -- Insert operation\n    INSERT INTO your_table (data) VALUES (p_data);\nEND secure_insert;\n/