-- Step 1: Disable foreign key checks\nSET foreign_key_checks = 0;\n\n-- Step 2: Drop the foreign key constraint in the child table\nALTER TABLE `t23_aluno` DROP FOREIGN KEY `FK_t23_aluno_8`;\n\n-- Step 3: Alter the column type in both tables\nALTER TABLE `t21_turma` MODIFY COLUMN `A21_ID` int(10) UNSIGNED NOT NULL AUTO_INCREMENT;\nALTER TABLE `t23_aluno` MODIFY COLUMN `a21_saida_id` int(10) UNSIGNED DEFAULT NULL;\n\n-- Step 4: Add the foreign key constraint back\nALTER TABLE `t23_aluno` \nADD CONSTRAINT `FK_t23_aluno_8` \nFOREIGN KEY (`a21_saida_id`) \nREFERENCES `t21_turma` (`A21_ID`);\n\n-- Step 5: Re-enable foreign key checks\nSET foreign_key_checks = 1;