CREATE PROCEDURE UpdateUserField\n    @UserFieldNumber INT,\n    @UserFieldNewValue INT\nAS\nBEGIN\n    DECLARE @Sql NVARCHAR(MAX);\n    DECLARE @ColumnName NVARCHAR(50);\n\n    -- Determine the column name based on the input number\n    SET @ColumnName = CASE @UserFieldNumber\n                      WHEN 1 THEN 'UserField1'\n                      WHEN 2 THEN 'UserField2'\n                      -- Add more cases if there are more columns\n                      ELSE NULL\n                      END;\n    \n    -- Ensure the column name is valid\n    IF @ColumnName IS NOT NULL\n    BEGIN\n        -- Construct the dynamic SQL statement\n        SET @Sql = 'UPDATE MyTable SET ' + @ColumnName + ' = @UserFieldNewValue';\n        \n        -- Execute the dynamic SQL statement\n        EXEC sp_executesql @Sql, N'@UserFieldNewValue INT', @UserFieldNewValue;\n    END\n    ELSE\n    BEGIN\n        -- Handle the case where the column number is invalid\n        RAISERROR('Invalid column number', 16, 1);\n    END\nEND;