SELECT \n    TableName = t.TABLE_NAME,\n    IndexName = i.INDEX_NAME,\n    IndexType = CASE \n                   WHEN i.UNIQUE = 1 THEN 'Unique'\n                   WHEN i.PRIMARY_KEY = 1 THEN 'Primary Key'\n                   ELSE 'Non-Unique'\n                END,\n    ColumnName = c.COLUMN_NAME\nFROM \n    INFORMATION_SCHEMA.INDEXES i\nJOIN \n    INFORMATION_SCHEMA.TABLES t ON i.TABLE_NAME = t.TABLE_NAME\nJOIN \n    INFORMATION_SCHEMA.INDEX_COLUMNS c ON i.INDEX_NAME = c.INDEX_NAME\nORDER BY \n    t.TABLE_NAME, i.INDEX_NAME, c.COLUMN_NAME;