-- Using Code as PK and FK for short, stable codes\nCREATE TABLE Currencies (\n    Code CHAR(3) PRIMARY KEY,\n    Name VARCHAR(100)\n);\n\nCREATE TABLE Countries (\n    Code CHAR(2) PRIMARY KEY,\n    Name VARCHAR(100)\n);\n\n-- Example of a table referencing Currencies by code\nCREATE TABLE Transactions (\n    ID INT PRIMARY KEY,\n    Amount DECIMAL(10, 2),\n    CurrencyCode CHAR(3),\n    FOREIGN KEY (CurrencyCode) REFERENCES Currencies(Code)\n);