CREATE FUNCTION dbo.InsertRecords (@nRecords INT)\nRETURNS @ResultTable TABLE (ID INT)\nAS\nBEGIN\n    WITH NumberCTE AS\n    (\n        SELECT 1 AS Number\n        UNION ALL\n        SELECT Number + 1\n        FROM NumberCTE\n        WHERE Number < @nRecords\n    )\n    INSERT INTO @ResultTable (ID)\n    SELECT Number\n    FROM NumberCTE\n    OPTION (MAXRECURSION 0);\n\n    RETURN;\nEND;