-- Using INTEGER as PK and FK for more volatile or longer codes\nCREATE TABLE XXXObjectType (\n    ID INT PRIMARY KEY AUTO_INCREMENT,\n    Code VARCHAR(50),\n    Name VARCHAR(100),\n    ... -- additional attributes\n);\n\n-- Example of a table referencing XXXObjectType by ID\nCREATE TABLE SomeRelatedTable (\n    ID INT PRIMARY KEY,\n    XXXObjectTypeID INT,\n    FOREIGN KEY (XXXObjectTypeID) REFERENCES XXXObjectType(ID)\n);