-- Query for Organizational Units\nSELECT\n    distinguishedName,\n    name,\n    objectClass\nFROM OPENQUERY(ADSI, '\n    SELECT\n        distinguishedName,\n        name,\n        objectClass\n    FROM ''LDAP://name''\n    WHERE objectClass = ''organizationalUnit''\n    ORDER BY name\n') AS OUs;\n\n-- Query for Groups\nSELECT\n    distinguishedName,\n    name,\n    objectClass\nFROM OPENQUERY(ADSI, '\n    SELECT\n        distinguishedName,\n        name,\n        objectClass\n    FROM ''LDAP://name''\n    WHERE objectClass = ''group''\n    ORDER BY name\n') AS Groups;