-- Create the table without default value for EXPIRATION column\nCREATE TABLE T_NAME (\n    ID INTEGER PRIMARY KEY,\n    EXPIRATION TIMESTAMP\n);\n\n-- Create the BEFORE INSERT trigger to set the default value for EXPIRATION\nSET TERM ^ ;\n\nCREATE TRIGGER BI_T_NAME_EXPIRATION FOR T_NAME\nBEFORE INSERT\nAS\nBEGIN\n    IF (NEW.EXPIRATION IS NULL) THEN\n        NEW.EXPIRATION = CURRENT_TIMESTAMP + 30;\nEND^\n\nSET TERM ; ^