SELECT\n    voterfile_county.Name AS County_Name,\n    voterfile_precienct.PREC_ID AS Prec_ID,\n    voterfile_precienct.Name AS Prec_Name,\n    COUNT(voterfile_voter.ID) AS Voters\nFROM\n    voterfile_precienct\nJOIN\n    voterfile_county ON voterfile_precienct.County_ID = voterfile_county.ID\nLEFT JOIN\n    voterfile_household ON voterfile_household.Precnum = voterfile_precienct.PREC_ID\nLEFT JOIN\n    voterfile_voter ON voterfile_voter.House_ID = voterfile_household.ID\nGROUP BY\n    voterfile_county.Name,\n    voterfile_precienct.PREC_ID,\n    voterfile_precienct.Name;