-- Example: Query for admin users\nSELECT u.*, a.admin_level\nFROM users u\nJOIN admin_details a ON u.user_id = a.user_id\nWHERE u.user_type = 'admin';\n\n-- Example: Query for customer users\nSELECT u.*, c.loyalty_points\nFROM users u\nJOIN customer_details c ON u.user_id = c.user_id\nWHERE u.user_type = 'customer';\n\n-- Example: Query for vendor users\nSELECT u.*, v.company_name\nFROM users u\nJOIN vendor_details v ON u.user_id = v.user_id\nWHERE u.user_type = 'vendor';