UPDATE PayrollTotals ptm\nSET PTAmount = PTAmount + \n    (SELECT pts.PTAmount \n     FROM PayrollTotals pts \n     WHERE pts.PTACID = 38 \n       AND pts.PTEmpID = ptm.PTEmpID \n       AND pts.PTAmount > 0)\nWHERE PTACID = 42\n  AND EXISTS (SELECT 1 \n              FROM PayrollTotals pts \n              WHERE pts.PTACID = 38 \n                AND pts.PTEmpID = ptm.PTEmpID \n                AND pts.PTAmount > 0);