BEGIN TRANSACTION;\n\n-- Delete areas related to regions in the specified country\nDELETE FROM Areas\nWHERE RegionID IN (\n    SELECT Region_ID FROM Regions WHERE Country_ID = @CountryID\n);\n\n-- Delete regions related to the specified country\nDELETE FROM Regions\nWHERE Country_ID = @CountryID;\n\n-- Delete the country\nDELETE FROM Country\nWHERE Country_ID = @CountryID;\n\nCOMMIT;