-- Step 1: Create a new table with the new column at the beginning\nCREATE TABLE NewTable (\n    NewColumn INT,  -- New column added at the beginning\n    ExistingColumn1 DATATYPE,  -- Replace DATATYPE with the actual data type\n    ExistingColumn2 DATATYPE  -- Add other existing columns here\n    -- Add all other existing columns in the desired order\n);\n\n-- Step 2: Copy data from the existing table to the new table\nINSERT INTO NewTable (NewColumn, ExistingColumn1, ExistingColumn2)\nSELECT \n    NULL AS NewColumn,  -- Assuming NewColumn is nullable, otherwise provide a default value\n    ExistingColumn1,\n    ExistingColumn2\nFROM ExistingTable;\n\n-- Step 3: Drop the existing table\nDROP TABLE ExistingTable;\n\n-- Step 4: Rename the new table to the original table name\nEXEC sp_rename 'NewTable', 'ExistingTable';