DECLARE @XmlData XML\nSET @XmlData = '<Configuration><SomeConfigValue></SomeConfigValue></Configuration>'\n\nDECLARE @SomeConfigValue BIT\n\n-- Extract the value as a string first\nDECLARE @SomeConfigValueStr NVARCHAR(10)\nSET @SomeConfigValueStr = @XmlData.value('(/Configuration/SomeConfigValue)[1]', 'NVARCHAR(10)')\n\n-- Convert the string to a bit or set it to NULL if it's empty\nSET @SomeConfigValue = CASE\n    WHEN @SomeConfigValueStr = '' THEN NULL\n    WHEN @SomeConfigValueStr = '1' THEN 1\n    WHEN @SomeConfigValueStr = '0' THEN 0\n    ELSE NULL\nEND\n\n-- Use the value in the update statement\nUPDATE MyTable\nSET ConfigValue = CASE\n    WHEN @SomeConfigValue IS NULL THEN T.SomeConfigValue\n    ELSE @SomeConfigValue\nEND\nFROM MyTable T