DECLARE\n  max_trans_seq_no NUMBER;\nBEGIN\n  -- Get the maximum value from the TRANSACTION_LOG table\n  SELECT MAX(trans_seq_no) INTO max_trans_seq_no FROM TRANSACTION_LOG;\n\n  -- Adjust to start with the next value\n  max_trans_seq_no := max_trans_seq_no + 1;\n\n  -- Create the sequence with the calculated start value\n  EXECUTE IMMEDIATE 'CREATE SEQUENCE transaction_sequence MINVALUE 0 START WITH ' || max_trans_seq_no || ' INCREMENT BY 1 CACHE 20';\nEND;\n/