WITH DescriptionCounts AS (\n    SELECT \n        id, \n        description, \n        COUNT(*) AS description_count\n    FROM \n        your_table\n    GROUP BY \n        id, \n        description\n), RankedDescriptions AS (\n    SELECT \n        id, \n        description, \n        description_count,\n        ROW_NUMBER() OVER (PARTITION BY id ORDER BY description_count DESC) AS rank\n    FROM \n        DescriptionCounts\n)\nSELECT \n    id, \n    description AS most_popular_description, \n    description_count AS times_the_desc_appeared_for_an_id\nFROM \n    RankedDescriptions\nWHERE \n    rank = 1;