-- Sample table and data\nCREATE TABLE TimeIntervals (\n    IntervalTime TIME\n);\n\nINSERT INTO TimeIntervals (IntervalTime)\nVALUES ('00:08:00'), ('00:07:00'), ('01:59:00');\n\n-- Query to round to the nearest 15-minute interval\nSELECT \n    IntervalTime, \n    DATEADD(MINUTE, (DATEDIFF(MINUTE, 0, IntervalTime) / 15) * 15 \n    + CASE WHEN DATEDIFF(MINUTE, 0, IntervalTime) % 15 >= 8 THEN 15 ELSE 0 END, 0) AS RoundedTime\nFROM \n    TimeIntervals;