DELIMITER //\n   CREATE PROCEDURE SimpleCursor()\n   BEGIN\n       DECLARE done INT DEFAULT 0;\n       DECLARE myVar INT;\n       DECLARE cur CURSOR FOR SELECT column FROM my_table;\n       DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;\n\n       OPEN cur;\n\n       read_loop: LOOP\n           FETCH cur INTO myVar;\n           IF done THEN\n               LEAVE read_loop;\n           END IF;\n           -- Do something with myVar\n       END LOOP;\n\n       CLOSE cur;\n   END //\n   DELIMITER ;