CREATE TABLE sheets (\n    sheet_id INT AUTO_INCREMENT PRIMARY KEY,\n    sheet_name VARCHAR(255) UNIQUE NOT NULL\n);\n\nCREATE TABLE rows (\n    row_id INT AUTO_INCREMENT PRIMARY KEY,\n    sheet_id INT NOT NULL,\n    row_name VARCHAR(255) NOT NULL,\n    FOREIGN KEY (sheet_id) REFERENCES sheets(sheet_id) ON DELETE CASCADE\n);\n\nCREATE TABLE cells (\n    cell_id INT AUTO_INCREMENT PRIMARY KEY,\n    row_id INT NOT NULL,\n    col_name VARCHAR(255) NOT NULL,\n    value VARCHAR(255),\n    FOREIGN KEY (row_id) REFERENCES rows(row_id) ON DELETE CASCADE\n);