DECLARE @ORDERBY INT\nSET @ORDERBY = 1\n\nSELECT TOP 10 * \nFROM TBL_LMS_USERS_RECORDs_LAST_ATTEMPT\nORDER BY \nCASE \n    WHEN @OrderBy = 1 THEN CAST(s10_record_dow AS VARCHAR(100))\n    WHEN @OrderBy = 2 THEN CAST(pk_big_record_id AS VARCHAR(100))\n    ELSE CAST(s10_record_dow AS VARCHAR(100))\nEND