-- Step 1: Create a new table with the same structure as table1 and table2\nCREATE TABLE merged_table LIKE table1;\n\n-- Step 2: Insert data from table1 into merged_table\nINSERT INTO merged_table (column1, column2, ...)\nSELECT column1, column2, ...\nFROM table1;\n\n-- Step 3: Insert data from table2 into merged_table, handling primary key clashes\n-- Here, we assume the primary key is 'id' and we are using `AUTO_INCREMENT` to avoid clashes\nINSERT INTO merged_table (column1, column2, ...)\nSELECT column1, column2, ...\nFROM table2\nON DUPLICATE KEY UPDATE id = id + (SELECT MAX(id) FROM merged_table);\n\n-- Optional: If you need to reset the AUTO_INCREMENT value to the correct next value\nALTER TABLE merged_table AUTO_INCREMENT = (SELECT MAX(id) + 1 FROM merged_table);