-- Query to find constraints related to the column\nSELECT \n    OBJECT_NAME(object_id) AS TableName,\n    name AS ConstraintName \nFROM \n    sys.check_constraints \nWHERE \n    OBJECT_NAME(parent_object_id) = 'TableName' \n    AND definition LIKE '%MyField%';\n\n-- Query to find foreign keys related to the column\nSELECT \n    OBJECT_NAME(fk.parent_object_id) AS TableName, \n    fk.name AS ForeignKeyName \nFROM \n    sys.foreign_keys AS fk\nINNER JOIN \n    sys.foreign_key_columns AS fkc\n    ON fk.object_id = fkc.constraint_object_id\nWHERE \n    OBJECT_NAME(fk.parent_object_id) = 'TableName' \n    AND COL_NAME(fkc.parent_object_id, fkc.parent_column_id) = 'MyField';