SELECT 'B' METRIC, ROW_PRIORITY,\n       LAST_VALUE(col1 IGNORE NULLS) OVER (ORDER BY ROW_PRIORITY ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) col1,\n       LAST_VALUE(col2 IGNORE NULLS) OVER (ORDER BY ROW_PRIORITY ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) col2,\n       LAST_VALUE(col3 IGNORE NULLS) OVER (ORDER BY ROW_PRIORITY ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) col3\nFROM ZTEST;