-- Create a temporary table with an identity column\nCREATE TABLE #TempPuppyNames (\n    RowNumber INT IDENTITY(1,1),\n    Name VARCHAR(255)\n);\n\n-- Insert the results into the temporary table\nINSERT INTO #TempPuppyNames (Name)\nSELECT TOP 3 Name\nFROM PuppyNames\nORDER BY NumberOfVotes;\n\n-- Select the results with row numbers\nSELECT RowNumber, Name\nFROM #TempPuppyNames;\n\n-- Drop the temporary table\nDROP TABLE #TempPuppyNames;