DECLARE\n    v_fk_violation EXCEPTION;\n    PRAGMA EXCEPTION_INIT(v_fk_violation, -2292); -- ORA-02292: integrity constraint violated - child record found\nBEGIN\n    FOR fk IN (SELECT table_name, constraint_name \n               FROM user_constraints \n               WHERE constraint_type = 'R') LOOP\n        BEGIN\n            EXECUTE IMMEDIATE 'ALTER TABLE ' || fk.table_name || ' DISABLE CONSTRAINT ' || fk.constraint_name || ' KEEP INDEX';\n            EXECUTE IMMEDIATE 'ALTER TABLE ' || fk.table_name || ' ENABLE CONSTRAINT ' || fk.constraint_name || ' NOVALIDATE';\n        EXCEPTION\n            WHEN v_fk_violation THEN\n                DBMS_OUTPUT.PUT_LINE('Foreign key violation in table ' || fk.table_name || ' for constraint ' || fk.constraint_name);\n        END;\n    END LOOP;\nEND;\n/