DELIMITER //\n\nCREATE TRIGGER format_data_after_insert\nAFTER INSERT ON your_table_name\nFOR EACH ROW\nBEGIN\n    UPDATE your_table_name\n    SET your_column_name = CONCAT(\n        SUBSTRING(NEW.your_column_name, 1, 3), '/',\n        SUBSTRING(NEW.your_column_name, 4, 3), '/',\n        SUBSTRING(NEW.your_column_name, 7, 3), '/',\n        SUBSTRING(NEW.your_column_name, 10)\n    )\n    WHERE id = NEW.id;\nEND //\n\nCREATE TRIGGER format_data_after_update\nAFTER UPDATE ON your_table_name\nFOR EACH ROW\nBEGIN\n    UPDATE your_table_name\n    SET your_column_name = CONCAT(\n        SUBSTRING(NEW.your_column_name, 1, 3), '/',\n        SUBSTRING(NEW.your_column_name, 4, 3), '/',\n        SUBSTRING(NEW.your_column_name, 7, 3), '/',\n        SUBSTRING(NEW.your_column_name, 10)\n    )\n    WHERE id = NEW.id;\nEND //\n\nDELIMITER ;