CREATE OR REPLACE TRIGGER meas_pnt_update_trigger\nINSTEAD OF UPDATE ON meas_pnt\nFOR EACH ROW\nBEGIN\n  UPDATE meas_pnt_tab m\n  SET m.position = SDO_GEOMETRY(\n                      m.position.SDO_GTYPE,\n                      m.position.SDO_SRID,\n                      SDO_POINT_TYPE(:NEW.x_coordinate, :NEW.y_coordinate, NULL),\n                      m.position.SDO_ELEM_INFO,\n                      m.position.SDO_ORDINATES)\n  WHERE m.meas_key = :OLD.meas_key;\nEND;\n/